/*elements_common */
.un_flex{display:flex!important;flex-wrap:nowrap}.un_flex_hc{flex-direction:row;justify-content:center;text-align:center;align-items:center}.un_flex>div{flex:1 1 auto;max-width:100%}.un_container{position:relative;min-height:inherit!important;width:80%!important;max-width:100%;margin:3rem auto;padding:1rem 4rem;font-family:"Open Sans";line-height:1.3;font-size:1.3rem;text-align:center}.un_container>h1{font-size:2rem;margin:.3rem!important;font-variant:small-caps}.csch_dark_2{background:linear-gradient(326deg,rgba(65,63,61,.84),rgba(45,43,41,.7),rgba(65,63,71,.9));color:rgba(243,243,243,.9)!important;background-size:800% 800%;animation:animbg 12s ease infinite}@keyframes animbg{0%{background-position:0 26%}50%{background-position:100% 75%}100%{background-position:0 26%}0%{background-position:0 26%}50%{background-position:100% 75%}100%{background-position:0 26%}}.csch_subtle_3{background:rgba(147,128,108,.1)}@keyframes animbg{0%{background-position:0 26%}50%{background-position:100% 75%}100%{background-position:0 26%}}.un_widefull{width:100%;max-width:100%}.hidden{display:none!important}@media screen and (max-width:55.5em){.un_container{margin:0;padding:0}}

body{font-family:'Open Sans',sans-serif;color:#757b87;font-size:1em}h1{font-family:Eczar}h4{font-size:1rem}.text-greenish{color:#5ef9c6!important}

@media only screen and (max-width:568px){.m-2{margin:1rem 0!important}}@media screen and (max-width:55.5em){.un_flex_hc{flex-wrap:wrap}.un_flex_hc>*{margin:.5rem}}